package org.nexial.core.plugins.imageimport org.apache.commons.io.FileUtilsimport org.apache.commons.lang3.StringUtilsimport org.junit.Testimport org.nexial.commons.utils.ResourceUtilsimport org.nexial.core.model.MockExecutionContextimport java.io.Fileimport kotlin.test.assertNotEqualsimport kotlin.test.assertTrueclass ImageMagickHelperManualTest {    private val resourceBasePath = StringUtils.replace(this.javaClass.getPackage().name, ".", "/")    private val context: MockExecutionContext? = object : MockExecutionContext(true) {}    @Test    fun convertToGrayscale() {        val imageFile2 = ResourceUtils.getResourceFilePath("$resourceBasePath/quality.png")        val helper = ImageMagickHelper()        val grayImagePath = helper.convertToGrayscale(imageFile2, context!!)        val grayFile = File(grayImagePath)        assertNotEquals(imageFile2, grayImagePath)        assertTrue(grayFile.name.startsWith("temp_gray_"))        FileUtils.deleteQuietly(grayFile)    }}